import axios from 'axios';

// Define the base URL for your API
const API_BASE_URL = 'http://localhost:5000/api/';


// Function to get user data
export const get_User = async (userId) => {
    try {
        const response = await axios.get(`${API_BASE_URL}users/${userId}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Function to get user data
export const get_Users = async () => {
    try {
        const response = await axios.get(`${API_BASE_URL}users`);
        console.log("response.data = " + response.data)
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Function to add a new user
export const add_User = async (userData) => {
    try {
        const response = await axios.post(`${API_BASE_URL}users/`, userData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Update an existing user
export const update_User = async (userId, updatedData) => {
    try {
        const response = await axios.put(`${API_BASE_URL}users/${userId}`, updatedData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Delete a user
export const delete_User = async (userId) => {
    try {
        const response = await axios.delete(`${API_BASE_URL}users/${userId}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};
