import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import EditUserForm from './EditUserForm';
import { get_Users, update_User, add_User, delete_User } from './api';

function App() {
  const [data, setData] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers(); 
  }, []);


  const fetchUsers = async () => {
    try {
      const response =  await get_Users();     
      setData(response)      
    } catch (error) {
      console.error("Error fetching data: ", error)
    }

  };


  const addUser = async (user) => {
    const response = await add_User(user);
    if (response['user_id']) {
      fetchUsers();
    }
  };

  const updateUser = async (userId, user) => {
    console.log('user = ', user)
    const response = await update_User(userId, user);
    
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (userId) => {
    const response = await delete_User(userId);
    console.log(response.success)
    if (response.success) {
      fetchUsers();
    }
  };


  const handleEdit = (user) => {
    setSelectedUser(user);
  };

  const handleUpdate = async (updatedUserData) => {
    try {
      // Make a PUT request to update the user's data
      const response = await axios.put(`${API_BASE_URL}/${selectedUser._id}`, updatedUserData);
      // Update the users list with the updated user
      const updatedUsers = users.map((user) =>
        user._id === selectedUser._id ? response.data : user
      );
      setUsers(updatedUsers);
      setSelectedUser(null); // Clear the selected user
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div className="container">
      <UserList data={data} onDelete={deleteUser}  />
      <UserForm onAdd={addUser} />
      <EditUserForm user={selectedUser} onUpdate={handleUpdate} />
    </div>
  );
}

export default App;
