import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import axios from 'axios';
import { get_Users, update_User, add_User, delete_User } from './api';

const API_BASE_URL = 'http://localhost:5000/api/users';

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    
    fetchUsers();
    users.forEach(user => {
      console.log("user.first_name = " + user.first_name)
    });

    fetchUsers();
  }, []);

  useEffect(() => {
    console.log('users.length =', users.length);
  }, [users]);


  const fetchUsers = async () => {
    try {
      const response =  await get_Users();
      response.forEach(user => {
        console.log("user.first_name = " + user.first_name)
      });
      setUsers(response)
      /* axios.get('http://localhost:5000/api/users')
           .then((response) => setUsers(response.data))
           .catch((error) => console.error('Error fetching data:', error)); */
      
    } catch (error) {
      console.error("Error fetching users: ", error)
    }

  };


  const addUser = async (user) => {
    const response = await add_User(user);
    if (response.user_id) {
      fetchUsers();
    }
  };

  const updateUser = async (user) => {
    const response = await update_User(user._id, user);
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (user) => {
    const response = await delete_User(user._id);
    if (response.data.success) {
      fetchUsers();
    }
  };

  return (
    <div className="container">
      <h1>Address Book</h1>
      <UserList users={users} onDelete={deleteUser} onUpdate={updateUser} />
      <UserForm onAdd={addUser} />
    </div>
  );
}

export default App;
