import axios from 'axios';

// Define the base URL for your API
const API_BASE_URL = 'http://localhost:5000/api';


// Function to get user data
export const getUser = async (userId) => {
    try {
        const response = await axios.get(`${API_BASE_URL}/get_users/${userId}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Function to get user data
export const getUsers = async () => {
    try {
        const response = await axios.get(`${API_BASE_URL}/get_users`);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Function to add a new user
export const addUser = async (userData) => {
    try {
        const response = await axios.post(`${API_BASE_URL}/add_user`, userData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Update an existing user
export const updateUser = async (userId, updatedData) => {
    try {
        const response = await axios.put(`${API_BASE_URL}/update_user/${userId}`, updatedData);
        return response.data;
    } catch (error) {
        throw error;
    }
};

// Delete a user
export const deleteUser = async (userId) => {
    try {
        const response = await axios.delete(`${API_BASE_URL}/delete_user/${userId}`);
        return response.data;
    } catch (error) {
        throw error;
    }
};
