import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import { get_Users, update_User, add_User, delete_User } from './api';

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers =  () => {
      try {
        const response =  get_Users();
        
        // Update the users state using a callback
        setUsers(prevUsers => [...prevUsers, ...response]);
        users.forEach(user => {
          console.log("user.first_name = " + user.first_name)
        });
        console.log("users = " + users)
        console.log("users.length = " + users.length)
      } catch (error) {
        console.error("Error fetching users: ", error)
      }

    };
    fetchUsers();

    

  }, []);




  const addUser = async (user) => {
    const response = await add_User(user);
    if (response.user_id) {
      fetchUsers();
    }
  };

  const updateUser = async (user) => {
    const response = await update_User(user._id, user);
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (user) => {
    const response = await delete_User(user._id);
    if (response.data.success) {
      fetchUsers();
    }
  };

  return (
    <div className="container">
      <h1>Address Book</h1>
      <UserList users={users} onDelete={deleteUser} onUpdate={updateUser} />
      <UserForm onAdd={addUser} />
    </div>
  );
}

export default App;
