import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import { get_Users, update_User, add_User, delete_User } from './api';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    data.forEach(user => {
      console.log("user._id 2 = " + user.first_name)
    });
  }, [data]);


  const fetchUsers = async () => {
    try {
      const response = await get_Users();
      setData(response)
    } catch (error) {
      console.error("Error fetching data: ", error)
    }

  };


  const addUser = async (user) => {
    const response = await add_User(user);
    if (response.user_id) {
      fetchUsers();
    }
  };

  const updateUser = async (user) => {
    const response = await update_User(user._id, user);
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (user) => {
    const response = await delete_User(user._id);
    if (response.data.success) {
      fetchUsers();
    }
  };

  return (
    <div className="container">
      <table className="table table-bordered table-striped table-hover">
        <thead className="table-dark">
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Address</th>
            <th>City</th>           
            <th>State/Province</th>
            <th>Zip/PostalCode</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          <UserList data={data} onDelete={deleteUser} onUpdate={updateUser} />
        </tbody>
      </table>
      <UserForm onAdd={addUser} />
    </div>
  );
}

export default App;
