import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import axios from 'axios';
import { get_Users, update_User, add_User, delete_User } from './api';

const API_BASE_URL = 'http://localhost:5000/api/data';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchUsers();
    data.forEach(user => {
      console.log("user.first_name 2 = " + user.first_name)
    });
  }, []);

  /* useEffect(() => {
    data.forEach(user => {
      console.log("user.first_name 2 = " + user.first_name)
    });
  }, [data]); */


  const fetchUsers = async () => {
    try {
      const response =  await get_Users();
      setData(response)      
    } catch (error) {
      console.error("Error fetching data: ", error)
    }

  };


  const addUser = async (user) => {
    const response = await add_User(user);
    if (response.user_id) {
      fetchUsers();
    }
  };

  const updateUser = async (user) => {
    const response = await update_User(user._id, user);
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (user) => {
    const response = await delete_User(user._id);
    if (response.data.success) {
      fetchUsers();
    }
  };

  return (
    <div className="container">
      <h1>Address Book</h1>
      <UserList data={data} onDelete={deleteUser} onUpdate={updateUser} />
      <UserForm onAdd={addUser} />
    </div>
  );
}

export default App;
