import React, { useState, useEffect } from 'react';
import UserList from './UserList';
import UserForm from './UserForm';
import axios from 'axios';
import { get_Users, update_User, add_User, delete_User } from './api';

const API_BASE_URL = 'http://localhost:5000/api/users';

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    
    //fetchUsers();
    users.forEach(user => {
      console.log("user.first_name = " + user.first_name)
    });

  }, [users]);


  const fetchUsers =  () => {
    try {
      //const response =  get_Users();
      res = axios.get('http://localhost:5000/api/users')
           .then((response) => setUsers(response?.data))
           .catch((error) => console.error('Error fetching data:', error));

      setUsers(res)
      
      
      console.log("users = " + res)
      console.log("users.length = " + res.length)
    } catch (error) {
      console.error("Error fetching users: ", error)
    }

  };


  const addUser = async (user) => {
    const response = await add_User(user);
    if (response.user_id) {
      fetchUsers();
    }
  };

  const updateUser = async (user) => {
    const response = await update_User(user._id, user);
    if (response.data.success) {
      fetchUsers();
    }
  };

  const deleteUser = async (user) => {
    const response = await delete_User(user._id);
    if (response.data.success) {
      fetchUsers();
    }
  };

  return (
    <div className="container">
      <h1>Address Book</h1>
      <UserList users={users} onDelete={deleteUser} onUpdate={updateUser} />
      <UserForm onAdd={addUser} />
    </div>
  );
}

export default App;
