import React, { useState } from 'react';
import { get_Users, update_User, add_User, delete_User } from './api';

function EditUserForm({ user, onCancelEdit }) {
  const [formData, setFormData] = useState({
    FirstName: user.FirstName,
    LastName: user.LastName,
    PrimaryAddress: user.PrimaryAddress
  });


  const handleSaveChanges = async () => {
    // Make a PUT request to update the user's data
    //await axios.put(`${API_BASE_URL}/users/${user._id}`, formData);
    console.log('formData.FirstName = ', formData.FirstName)
    const response = await update_User(user._id, formData);
    console.log('response.success = ', response.success)

    onCancelEdit();

  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Form rendering and input fields go here

  return (
    <tr>
      <td>
        <input
          type="text"
          className="form-control m-2 col"
          id="FirstName"
          name="FirstName"
          value={formData.FirstName}
          onChange={handleInputChange}
        />
      </td>
      <td>
        <input
          type="text"
          className="form-control m-2 col"
          id="lastName"
          value={formData.LastName}
          onChange={(e) => setLastName(e.target.value)}
          required
          placeholder="Last Name"
        />
      </td>
      <td>
        <input
          type="text"
          className="form-control m-2 col"
          id="streetAddress"
          value={formData.PrimaryAddress.StreetAddress}
          onChange={(e) => setStreetAddress(e.target.value)}
          required
          placeholder="Street Address"
        />
      </td>
      <td >
        <input
          type="text"
          className="form-control m-2 col"
          id="city"
          value={formData.PrimaryAddress.City}
          onChange={(e) => setCity(e.target.value)}
          required
          placeholder="City"
        />
      </td>
      <td>
        <input
          type="text"
          className="form-control m-2 col"
          id="province"
          value={formData.PrimaryAddress.Province}
          onChange={(e) => setProvince(e.target.value)}
          required
          placeholder="Province"
        />
      </td>
      <td>
        <input
          type="text"
          className="form-control m-2 col"
          id="formData.PrimaryAddress.PostalCode"
          value={postalCode}
          onChange={(e) => setPostalCode(e.target.value)}
          required
          placeholder="Postal/Zip Code"
        />
      </td>
      <td>
        <button type="button" onClick={handleSaveChanges}>Save Changes</button>
        <button type="button" onClick={onCancelEdit}>Cancel</button>
      </td>
    </tr>

  );
}

export default EditUserForm;
